import React, { useState } from 'react';

import './UnsubscribeTopic.css';
import Card from '../UI/Card';

function UnsubscribeTopic(props) {
  const [enteredTopic, setEnteredTopic] = useState('');

  function updateTopicHandler(event) {
    setEnteredTopic(event.target.value);
  }
  const elements = props.topics;
  const items = []
  for (const [index, value] of elements.entries()) {
      items.push(<li key={index}>{value}</li>)
  }
  function topicSubmitHandler(event) {
    setEnteredTopic(event.target.value);
    event.preventDefault();
    if (enteredTopic.trim().length === 0) {
      alert('Invalid topic - please enter a topic name');
      return;
    }
    if (elements.entries().includes(enteredTopic)) {
        alert('Invalid topic - please enter a topic name');
        return;
      }
    else{
      // alert('Successfully subscribed to ',event);
    }
    const userid = props.user.email;
    props.onDeleteTopic(userid,enteredTopic);


    setEnteredTopic('');
  }

  return (
    <section id='topic-input'>
      <Card>
        <form onSubmit={topicSubmitHandler}>
        <label htmlFor='text'>Enter topic you want to unsubscribe</label>
          <input
            type='text'
            id='text'
            value={enteredTopic}
            onChange={updateTopicHandler}
          />

  {items}
          <button type='submit' id="topic" name="topic">Unsubscribe </button>

        </form>
      </Card>
    </section>
  );
}

export default UnsubscribeTopic;
