import React, { useState ,useEffect} from 'react';
import TopicInput from './components/songs/TopicInput';
import PublishTopic from './components/songs/PublishTopic';
import LoginScreen from './components/LoginScreen/LoginScreen'
import ErrorAlert from './components/UI/ErrorAlert';
import SuccessAlert from './components/UI/Success';
import SubscribedTopic from './components/songs/SubscribedTopic'
import UnsubscribeTopic from './components/songs/UnsubscribeTopic';

function App() {


  const [user, setEnteredUser] = useState({ email: "", pwd: "" });
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [topicsSubscribed, setTopicsSubscribed] = useState([]);

  var topicsSubscribe = []
  useEffect(function () {
    async function fetchData(user) {


      try {

        const response = await
          fetch('http://localhost/filtertopic/' + user.email,
            {
              method: 'GET',
            });
        console.log(response)

        // response.json().then(function (result) {

        //   topicsSubscribe.splice(0, topicsSubscribe.length)
        //   result.forEach(element => {
        //     console.log(element)

        //     topicsSubscribe.push(element)


        //     console.log('topicsssss' + topicsSubscribed)
        //   })// "Some details token"
        //   setTopicsSubscribed(topicsSubscribe)
        // })






      } catch (err) {
        setError(
          err.message ||
          'Subscribing a topic failed - the server responsed with an error.'
        )}

        }

    fetchData();
  }, []);
  async function addTopicHandler(userid, topicText) {


    try {
      const response = await fetch('http://localhost/topics', {
        method: 'POST',
        body: JSON.stringify({
          userid: userid,
          topic: topicText,
        }),
        headers: {
          'Content-Type': 'application/json'
        }
      });

      const resData = await response.json();

      if (!response.ok) {
        throw new Error(resData.message || 'Subscribing the topic failed.');
      }
      else {
        setSuccess('Successfully subscribed to Topic')
        console.log('successfluyy subscribe')


      }
      setTimeout(() => {
        setError(null)
      }, 100);


    } catch (err) {
      setError(
        err.message ||
        'Adding a topic failed - the server responsed with an error.'
      );
    }

  }
  async function Login(details) {

    try {
      console.log(details)
      const response = await fetch('http://localhost/login',
        {
          method: 'POST',
          body: JSON.stringify({
            username: details.username,
            password: details.password,
          }),
          headers: {
            'Content-Type': 'application/json'
          }
        });
      const resData = await response.json();
      if (!response.ok) {
        throw new Error(resData.message || 'Invalid Details.');
      }
      else {
        setEnteredUser({
          email: details.username,
          pwd: details.password
        })

      }

    }
    catch (err) {
      setError(
        err.message ||
        'Subscribing a topic failed - the server responsed with an error.'
      );
    }

  }
  async function subscribeData(user) {
    try {

      const response = await
        fetch('http://localhost/filtertopic/' + user,
          {
            method: 'GET',
          });
      console.log(response)

      response.json().then(function (result) {

        topicsSubscribe.splice(0, topicsSubscribe.length)
        result.forEach(element => {
          console.log(element)

          topicsSubscribe.push(element)


          console.log('topicsssss' + topicsSubscribed)
        })// "Some User token"
        setTopicsSubscribed(topicsSubscribe)
      })






    } catch (err) {
      setError(
        err.message ||
        'Subscribing a topic failed - the server responsed with an error.'
      );
    }
  }


  return (

    <main className="App">
      {error && <ErrorAlert errorText={error} />}
      {success && <SuccessAlert successText={success} />}
      {(user.email === '') ?
        (<LoginScreen Login={Login} e={error} />)
        :
        (
          <><TopicInput onAddTopic={addTopicHandler} user={user}
           />
         <UnsubscribeTopic topics= {topicsSubscribed.map(home => <><div>{home.name}</div>
            </>)}/>


<PublishTopic onPublishTopic={subscribeData} user={user} />
       <SubscribedTopic 
            user={topicsSubscribed.map(home => <><div>{home.name}</div>
              <div>Symbol
                {home.symbol}</div>
              <div>PriceUSD{home.priceUsd}</div>
              <div>PriceBTC{home.priceBtc}</div>
              <div>percentChange24hUsd{home.percentChange24hUsd}</div>
              <div>LastUpdated{home.lastUpdated}</div></>)} />
        </>

        )
      }


    </main>

  );
}


export default App;
